import React, { useState } from 'react';
import './ProductConstructor.css';

function ProductConstructor() {
    // const [productName, setProductName] = useState('');
    // const [productDescription, setProductDescription] = useState('');
    // // Добавьте состояния для других полей формы по аналогии

    // const handleFormSubmit = (e) => {
    //     e.preventDefault();
    //     // Логика обработки отправки формы
    // };

    // return (
    //     <form onSubmit={handleFormSubmit}>
    //         <div className="left-block">
    //             <label htmlFor="productName">Название:</label>
    //             <input type="text" id="productName" value={productName} onChange={(e) => setProductName(e.target.value)} />
    //             <label htmlFor="productDescription">Редактор текста:</label>
    //             <textarea id="productDescription" value={productDescription} onChange={(e) => setProductDescription(e.target.value)} />
    //             {/* Добавьте элементы для прикрепления фото и видео */}
    //         </div>
    //         <div className="right-block">
    //             {/* Добавьте радио-кнопки, дату публикации и кнопку сабмита */}
    //         </div>
    //         <div className="left-block">
    //             {/* Добавьте чекбоксы категорий, окно поиска по тегам, инпуты для длины, веса и т.д. */}
    //         </div>
    //         <div className="right-block">
    //             {/* Добавьте окно поиска по другим товарам для upsells */}
    //         </div>
    //         <div className="left-block">
    //             {/* Добавьте инпуты для цены, артикула, размеров, количества и т.д. */}
    //         </div>
    //         <div className="right-block">
    //             {/* Добавьте чекбоксы выбора цвета, цвета камня, дополнительных свойств и т.д. */}
    //         </div>
    //         <button type="submit">Сохранить</button>
    //         {/* Дублирующая кнопка сабмита */}
    //     </form>
    // );

    const [productName, setProductName] = useState('');
    const [productDescription, setProductDescription] = useState('');
    const [category, setCategory] = useState([]);
    const [length, setLength] = useState('');
    const [weight, setWeight] = useState('');
    const [stoneOrigin, setStoneOrigin] = useState('');
    const [metalType, setMetalType] = useState('');
    const [stoneSize, setStoneSize] = useState('');
    const [production, setProduction] = useState('');
    const [price, setPrice] = useState('');
    const [salePrice, setSalePrice] = useState('');
    const [discount, setDiscount] = useState(0);
    const [articleNumber, setArticleNumber] = useState('');
    const [inStock, setInStock] = useState('');
    const [buttonColor, setButtonColor] = useState('');
    const [stoneColor, setStoneColor] = useState([]);
    const [additionalProperties, setAdditionalProperties] = useState([]);
    const [favorite, setFavorite] = useState(false);
    // const [status, setStatus] = useState('draft');
    const [radioValue, setRadioValue] = useState('');
    const [attachedFiles, setAttachedFiles] = useState([]);


    const handleFormSubmit = (e) => {
        e.preventDefault();

    };

      function handleDrop(e) {
        e.preventDefault();
        const files = [...e.dataTransfer.files];
        setAttachedFiles([...attachedFiles, ...files]);
      };

      function handleFileInputChange(e) {
        const files = [...e.target.files];
        setAttachedFiles([...attachedFiles, ...files]);
      };

      function handleAttachButtonClick() {
        document.getElementById('fileInput').click();
    };

    function handleCategoryChange(e) {
      setCategory(e.target.value)
    }

    return (
      <main className='product-constructor'>
          <form className='add-form' onSubmit={handleFormSubmit}>
            {/* <h2>Добавление и редактирование товара</h2> */}
            <div className='form-container'>


            <div className="left-block">
                  <input className='input_size_long input-title' type="text" id="productName" value={productName} placeholder='Название товара' onChange={(e) => setProductName(e.target.value)} />
                  <textarea className='input_size_long input_size_big' id="productDescription" value={productDescription} placeholder='Описание товара' onChange={(e) => setProductDescription(e.target.value)} />
                  {/* для фоток и видео */}
                  <div className="input-files" onDrop={handleDrop} onDragOver={(e) => e.preventDefault()}>
                    <input type="file" id="fileInput" multiple style={{ display: 'none' }} onChange={handleFileInputChange} />
                    {attachedFiles.map((file, index) => (
                        <div className='file-preview' key={index}>
                          {/* тут будут превью и всё такое */}
                          {console.log(file)}
                            {file.name}
                        </div>
                    ))}
                  
            </div>
            <button type="button" className='constructor-button' onClick={handleAttachButtonClick}>Прикрепить файлы</button>
              </div>


              <div className="right-block">
                <div className='status__container'>
                  <h3>Статус товара</h3>
                 
                  <label>
                      <input className='radio_type_normal' type="radio" value="1" checked={radioValue === '1'} onChange={() => setRadioValue('1')} />
                      Опубликовать
                  </label>
                  <label>
                      <input className='radio_type_normal' type="radio" value="0" checked={radioValue === '0'} onChange={() => setRadioValue('0')} />
                      Оставить черновик
                  </label>
                  {/* <input type="date" />  */}
                  {/* <a href="#" target="_blank">Посмотреть товар</a> */}
                  <button className='submit-adding' type="submit">Сохранить</button>
                </div>
              </div>

      
              <div className="left-block">
                  {/* ЧЕКБОКСЫ КАТЕГОРИЙ  */}
                <h3>Категория товара:</h3>
                <div className='cats-div'>
                  
                    <label htmlFor='cat-beads' className='cat-checkbox__label'>
                      <input className='cat-checkbox' type='checkbox' name='category' value={6} id='cat-beads' onChange={handleCategoryChange}/> 
                      <div className='pseudo-button pointer'>Бусы</div> 
                    </label>
                    <label htmlFor='cat-complects' className='cat-checkbox__label'>
                      <input className='cat-checkbox' type='checkbox' name='category' value={167} id='cat-complects' onChange={handleCategoryChange}/> 
                      <div className='pseudo-button pointer'>Комплекты</div>
                    </label>
                    <label htmlFor='cat-rings' className='cat-checkbox__label'>
                      <input className='cat-checkbox' type='checkbox' name='category' value={40} id='cat-rings' onChange={handleCategoryChange}/> 
                      <div className='pseudo-button pointer'>Кольца</div>
                    </label>
                    <label htmlFor='cat-earrings' className='cat-checkbox__label'>
                      <input className='cat-checkbox' type='checkbox' name='category' value={44} id='cat-earrings' onChange={handleCategoryChange}/> 
                      <div className='pseudo-button pointer'>Серьги</div>
                    </label>
                    <label htmlFor='cat-pendants' className='cat-checkbox__label'>
                      <input className='cat-checkbox' type='checkbox' name='category' value={102} id='cat-pendants' onChange={handleCategoryChange}/> 
                      <div className='pseudo-button pointer'>Подвески</div>
                    </label>
                    <label htmlFor='cat-braceletes' className='cat-checkbox__label'>
                      <input className='cat-checkbox' type='checkbox' name='category' value={25} id='cat-braceletes' onChange={handleCategoryChange}/> 
                      <div className='pseudo-button pointer'>Браслеты</div>
                    </label>
                    <label htmlFor='cat-necklaces' className='cat-checkbox__label'>
                      <input className='cat-checkbox' type='checkbox' name='category' value={28} id='cat-necklaces' onChange={handleCategoryChange}/> 
                      <div className='pseudo-button pointer'>Колье</div>
                    </label>
                    <label htmlFor='cat-broaches' className='cat-checkbox__label'>
                      <input className='cat-checkbox' type='checkbox' name='category' value={107} id='cat-broaches' onChange={handleCategoryChange}/> 
                      <div className='pseudo-button pointer'>Броши</div>
                    </label>
                </div>
                
                <div className='stone-input__container'>
                  <h3>Камни:</h3>
                  <input className='input_size_long input_size_medium' type="text" placeholder="Начни вводить название" />
                </div>
                
                {/* ИНПУТЫ ДЛЯ ХАРАКТЕРИСТИК ДЛЯ ТАБЛИЧКИ В ТОВАРЕ */}
                <div className='chars__container'>
                <h3>Характеристики:</h3>
                  <div className='chars__item'>
                    <label htmlFor='input-length' className=''>Длина в см:</label>
                    <input className='input_size_small' id='input-length' type="number" placeholder="22,5" value={length || ''} onChange={(e) => setLength(e.target.value)}/>
                  </div>

                  <div className='chars__item'>
                    <label htmlFor='input-weight' className=''>Вес в граммах:</label>
                    <input className='input_size_small' id='input-weight' type="number" placeholder="25" value={weight || ''} onChange={(e) => setWeight(e.target.value)}/>
                  </div>
                  
                  <div className='chars__item'>
                    <label htmlFor='input-origin' className=''>Происхождение камня:</label>
                    <input className='input_size_small' id='input-origin' type="text" placeholder="Якутия" value={stoneOrigin || ''} onChange={(e) => setStoneOrigin(e.target.value)}/>
                  </div>

                  <div className='chars__item'>
                    <label htmlFor='input-metal' className=''>Металл:</label>
                    <input className='input_size_small' id='input-metal' type="text" placeholder="Мельхиор" value={metalType || ''} onChange={(e) => setMetalType(e.target.value)}/>
                  </div>

                  <div className='chars__item'>
                    <label htmlFor='input-stonesize' className=''>Размер камня в см</label>
                    <input className='input_size_small' id='input-stonesize' type="text" placeholder="1,2 х 0,3" value={stoneSize || ''} onChange={(e) => setStoneSize(e.target.value)}/>
                  </div>

                  <div className='chars__item'>
                    <label htmlFor='input-production' className=''>Производство:</label>
                    <input className='input_size_small' id='input-production' type="text" placeholder="Россия, Екатеринбург" value={production || ''} onChange={(e) => setProduction(e.target.value)}/>
                  </div>
                

                 
                </div>
                  
              </div>
              <div className="right-block">
                <h3>Дополняющие товары (upsells)</h3>
                  <input type="text" placeholder="Поиск по другим товарам" />

              </div>
              <div className="left-block">

              </div>
              <div className="right-block">

              </div>
              <div className="left-block">

                {/* ИНПУТЫ ПО ЦЕНАМ, НАЛИЧИЮ */}
              <div className='chars__container'>
              <h3>Цена, наличие:</h3>
              <div className='chars__item'>
                <label htmlFor='input-price' className=''>Полная цена:</label>
                <input className='input_size_small' type="number" placeholder="3590" id='input-price' value={price} onChange={(e) => setPrice(e.target.value)} />
              </div>
              <div className='chars__item'>
                <label htmlFor='input-sale-price' className=''>Цена по скидке:</label>
                <input className='input_size_small' type="number" placeholder="Необязательно" id='input-sale-price' value={salePrice} onChange={(e) => setSalePrice(e.target.value)} />
              </div>
              <div className='chars__item'>
                <label htmlFor='input-discount' className=''>Размер скидки: {discount}%</label> {/*сделать чтобы автоматом заполнялась сейлпрайс*/}
                <input className='input_size_small' type="range" min="0" max="95" placeholder="0" id='input-discount' value={discount} onChange={(e) => setDiscount(e.target.value)} />
              </div>
              <div className='chars__item'>
                <label htmlFor='input-stock' className=''>Штук в наличии: {inStock}</label>
                <input className='input_size_small' type="number" placeholder="1" id='input-stock' value={inStock} onChange={(e) => setInStock(e.target.value)} />
              </div>
              </div>

                  <input type="text" placeholder="Артикул" value={articleNumber} onChange={(e) => setArticleNumber(e.target.value)} />
                  {/* <input type="number" placeholder="Количество в наличии" value={quantityInStock} onChange={(e) => setQuantityInStock(e.target.value)} /> */}
                  <button className='submit-adding' type="submit">Сохранить</button> {/* Дублирующая кнопка сабмита */}
              </div>
              <div className="right-block">
                  <label>Цвет кнопки товара:</label>
                  <label>
                      <input type="checkbox" value="" checked={buttonColor === 'red'} onChange={(e) => setButtonColor(e.target.checked ? 'red' : '')} />
                      Красный
                  </label>

              </div>
            
            </div>
            
          </form>
        </main>
    );
};

