import './GalleryPopup.css';
import closeIcon from '../../images/x.svg';

export default function GalleryPopup({ mainImage, images, isOpen, onClose }) {
  // console.log(images);
  return (
<div className={isOpen ? 'popup popup_opened' : 'popup'}>

  <div className='popup__container'>

    {
      images.map((pic) => { 
        return (
        <img className='popup__image' src={pic} id={images.indexOf(pic)}></img>
      )})
    }

    <div className='popup__preview-container'>
    {
      images.map((pic) => {
        return (
        <a href=''>
          <img className='popup__image-preview' src={pic}></img>
        </a>
        
      )})
    }
    </div>

  </div>
  <button type='button' className='close-button'>
    <img className='close-icon' src={closeIcon} onClick={onClose}></img>
  </button>
</div>
  )
}
