import { useState, useEffect } from "react";
import Catalog from "../Catalog/Catalog";
import productsApi from "../../utils/Api";
import Header from "../Header/Header";
import MobileNavBar from "../MobileNavBar/MobileNavBar";

export default function SavedMovies({ onCardClick, onLikeClick, savedProducts, terms, onCatClick, category }) {

  const [wishlist, setWishlist] = useState([])

// взять массив айдишников и сделать запрос 

useEffect(() => {
  productsApi.getProductsById([savedProducts])
    .then((res) => {
      setWishlist(res)
      // console.log(res)
    })
    .catch((err) => console.log(err))
}, [savedProducts])

// console.log(wishlist)

  return (
    <>
    <Header 
    terms={terms}
    onCatClick={onCatClick}
    activeCategory={category}
    />
    <h1 className="wishlist-header">Избранное</h1>
    <Catalog
    onCardClick={onCardClick}
    onLikeClick={onLikeClick}
    savedProducts={savedProducts}
    products={wishlist}
  />
  <MobileNavBar/>
  </>
  )
}
