import './CartProduct.css';
import { useEffect, useState } from 'react';

export default function CartProduct({ name, price, onCardClick, pictures, id, cartDetails, onRemoveClick, savedProducts, onLikeClick }) {

  // mainPicture = mainPicture.replace(/'/g, ''); // kolkhoz
  const mainPicture = `https://dolina.shop/photos/${pictures[0].path.slice(0, -4) + `_preview.jpg`}`;
  const [isChecked, setIsChecked] = useState(true);
  
  function handleClick() {
    onCardClick(id)
  }

  function onCheckboxClick() {
    isChecked = !isChecked;
  }

  function onLike() {
    onLikeClick(id)
  }

  function onRemove() {
    onRemoveClick(id);
  }

  return (

       <li className='cart-product'>
        <div className='flex_type_row'>
          <label htmlFor={`${`cart-checkbox` + id}`} className='cart-product__checkbox-container'>
            <input type='checkbox' name={`${`cart-checkbox` + id}`} className='cart-product__checkbox' checked={isChecked} onClick={onCheckboxClick}></input>
            <div className='cart-product__pseudo-checkbox pointer'></div>
          </label>
          <a href='#' className='cart-product__image-container'>
            <div className='card__image-overlay'></div>
            <img className='cart-product__image' src={mainPicture} onClick={handleClick}></img>
          </a>
          <div className='cart-product__info'>
            <a className='cart-product__title' href='#'><h3 className='cart-product__title'>{name}</h3></a>
            {cartDetails && <p className='cart-product__size'>Размер: {cartDetails.size}</p>}
            <div className='cart-product__buttons'>
              <button type='button' className={(savedProducts.includes(id) ? 'cart-product__button cart-product__like-button_active' : 'cart-product__button cart-product__like-button')} onClick={onLike}></button>
              {/* <button type='button' className={(savedProducts.includes(productData.productId) ? 'card__save-button card__save-button_active' : 'card__save-button')} onClick={handleLike}></button> */}

              <button type='button' className='cart-product__button cart-product__delete-button' onClick={onRemove}></button>
            </div>
          </div>
          
        </div>
        <div className='cart-product__price-container'>
          <p className='cart-product__price'>{price} &#8381;</p>
        </div>
    </li>
  )
}
