import './Catalog.css';
import Card from '../Card/Card';
import { useParams } from 'react-router-dom';
// import {useState, useEffect, useContext } from 'react';
// import { CurrentUserContext } from '../../contexts/CurrentUserContext'; 
import productsApi from '../../utils/Api';
// import Product from '../Product/Product';
import { useState, useEffect } from 'react';

export default function Catalog({ onCardClick, products, setProducts, onLikeClick, savedProducts, search, setSearch}) {
  // console.log(products[0])

  
  const [offset, setOffset] = useState(24);
  const [loading, setLoading] = useState(false);
  const pageSize = 24;

  const fetchProducts = async () => {
      setLoading(true);
      try {
          const data = await productsApi.filterProducts('', '', '', offset, pageSize);
          setProducts(prevProducts => [...prevProducts, ...data]);
          setOffset(prevOffset => prevOffset + pageSize);
      } catch (error) {
          console.error('Error fetching products', error);
      }
      setLoading(false);
  };

  return (
    
    <section className='catalog'>
      <ul className='catalog-list'>
        {products.map((product) => 
        (
          <Card
          // product={product}
            id={product.productId}
            slug={product.slug}
            name={product.title}
            // mainPicture={product.pictures[0]}
            price={product.price}
            key={product.productId}
            onCardClick={onCardClick}
            onLikeClick={onLikeClick}
            pictures={product.pictures}
            savedProducts={savedProducts}
            inStock={product.inStock}
            search={search}
            setSearch={setSearch}
            createdAt={product.createdAt}
            discount={product.discount}
            salePrice={product.salePrice}
            // isLoggedIn={isLoggedIn}
          ></Card>
        ))}
      </ul>

      <div className='load_more_button'>
                <button onClick={() => fetchProducts()} disabled={loading}>
                    {loading ? 'грузится...' : 'загрузить ещё'}
                </button></div>

    </section>
  )
}
