import './Card.css';
import { useEffect, useState } from 'react';
import { useMediaQuery } from 'react-responsive';
import MobileSlider from './MobileSlider';

export default function Card({ name, mainPicture, price, onCardClick, onLikeClick, id, slug, pictures, isUnique, savedProducts }) {

  const isBigScreen = useMediaQuery({ minWidth: 768 });
  const isSmallScreen = useMediaQuery({ maxWidth: 767 });

  // mainPicture = mainPicture.replace(/'/g, ''); // kolkhoz
  // pictures = JSON.parse(pictures); // это должно быть в api 
  const [previewPicture, setPreviewPicture] = useState(mainPicture.path); //поднять стейт?

  function handleClick() {
    let newSlug = slug + "-" + id;
    onCardClick(newSlug)
  }

  function handleLike(e) {
    e.preventDefault();
    onLikeClick(id);
  }



  return (
    <li className='card pointer' key={id}>
      <div className='card__container'>
        <a href=''>
          {/* условный рендеринг в зависимости от размера экрана */}
          {isBigScreen && (
                      <div className='card__image-container' onMouseLeave={() => { setPreviewPicture(mainPicture) }}>
                      <div className='card__previews'>
                        {pictures.map((pic) => (
                          <div className='card__preview-container' key={pictures.indexOf(pic)}> 
                            <div className='card__preview-mark'></div> 
                            <img className='card__preview' 
                            src={pic.path} 
                            onClick={handleClick}
                            onMouseOver={() => {
                              setPreviewPicture(pic)
                            }}
                          ></img>
                          </div>
                        ))}
                      </div>
                      <button type='button' className={(savedProducts.includes(id) ? 'card__save-button card__save-button_active' : 'card__save-button')} onClick={handleLike}></button>
                      <div className='card__image-overlay'></div>
                      <img className='card__image' src={previewPicture}/>
                    </div>
          )}


          {isSmallScreen && (
                      <div className='card__mobile-container' onClick={handleClick}>
                      <MobileSlider pictures={pictures}/>
                      <button type='button' className={(savedProducts.includes(id) ? 'card__save-button card__save-button_active' : 'card__save-button')} onClick={handleLike}></button>
                      <div className='card__image-overlay'></div>
                    </div>
          )}

        </a>
        <div className='card__description-area' onClick={handleClick}>
          <span className='card__price'>{price} &#8381;</span>
          <h2 className='card__header'>{name}</h2>
        </div>
      </div>
    </li>
  )
}
