import './Card.css';
import { useEffect, useState } from 'react';

export default function Card({ name, mainPicture, price, onCardClick, id, pictures, key }) {

  mainPicture = mainPicture.replace(/'/g, ''); // kolkhoz
  pictures = JSON.parse(pictures); // это должно быть в api 
  const [previewPicture, setPreviewPicture] = useState(mainPicture); //поднять стейт

  function handleClick() {
    onCardClick(id)
  }

  function onHover() {

  }

  return (
    <li className='card' key={key} onClick={handleClick}>
      <a href='#'>
        <div className='card__image-container'>
          <div className='card__previews'>
            {pictures.map((pic) => (
              // <div className='card__preview-container'> с доп дивом не срабатывает больше одного раза на картинке
                // {/* <div className='card__preview-mark'></div> */} 
                <img className='card__preview' src={pic} onMouseOver={(evt) => {
                setPreviewPicture(pic)
                // console.log({pic})
              }}></img>
              
              // </div>
            ))}
          </div>
          <button type='button' className='card__save-button'></button>
          <div className='card__image-overlay'></div>
          <img className='card__image' src={previewPicture}/>
        </div>
      </a>
      <div className='card__description-area'>
        <span className='card__price'>{price} &#8381;</span>
        <h2 className='card__header'>{name}</h2>
      </div>
    </li>
  )
}
