import { useEffect, useState } from 'react';
import { useMediaQuery } from 'react-responsive';
import Skeleton from 'react-loading-skeleton';
import 'react-loading-skeleton/dist/skeleton.css';

export default function UpsellCard({ name, price, onCardClick, onLikeClick, id, slug, pictures, isUnique, savedProducts }) {

  const mainPicture = `https://dolina.shop/photos/${pictures[0].path.slice(0, -4) + `_preview.jpg`}`; // делаем полный путь конкатенацией
  // const [previewPicture, setPreviewPicture] = useState(mainPicture);
  const isBigScreen = useMediaQuery({ minWidth: 771 }); // надо бы перенести в константы
  const isSmallScreen = useMediaQuery({ maxWidth: 770 });

  function handleClick() {
    let newSlug = slug + "-" + id;
    onCardClick(newSlug)
  }

  function handleLike(e) {
    e.preventDefault();
    onLikeClick(id);
  }

  return (

    <li className='upsell-card pointer' key={id}>
      {isBigScreen && (!pictures ? (
        <div className='upsell-card__container'>
          <Skeleton height={336} width={200} />
        </div>
      ) : (
        <div className='upsell-card__container'>
          {/* <a href=''> */}
          <div className='upsell__image-container'>
            <div className=''>
            </div>
            <button type='button' className={(savedProducts.includes(id) ? 'card__save-button card__save-button_active' : 'card__save-button')} onClick={handleLike}></button>
            <div className='card__image-overlay'></div>
            <img className='upsell-card__image' src={mainPicture} onClick={handleClick} />
          </div>
          <div className='card__description-area' onClick={handleClick}>
            <span className='card__price'>{price} &#8381;</span>
            <h2 className='card__header'>{name}</h2>
          </div>
          {/* </a> */}
        </div>
      )
        
      )}
      {isSmallScreen && (
        <div className='upsell-card__container'>
          {/* <a href=''> */}
          <div className='upsell__image-container'>
            <div className=''>
            </div>
            <button type='button' className={(savedProducts.includes(id) ? 'card__save-button card__save-button_active' : 'card__save-button')} onClick={handleLike}></button>
            <div className='card__image-overlay'></div>
            <img className='upsell-card__image' src={mainPicture} onClick={handleClick} />
          </div>
          <div className='card__description-area' onClick={handleClick}>
            <span className='card__price'>{price} &#8381;</span>
            <h2 className='card__header'>{name}</h2>
          </div>
          {/* </a> */}
        </div>
      )}


    </li>
  )
}
