import './Tags.css';
import { useEffect, useState, useRef } from 'react';
import caret from '../../images/caret-down_mini.svg';

export default function Tags({ tags, categories, onTagClick, activeCategory, activeTags }) {
  const [selectedTags, setSelectedTags] = useState([]);
  const [filteredTags, setFilteredTags] = useState([]);
  const [dropdownStates, setDropdownStates] = useState({ 0: false, 1: false }); // Используем объект для хранения состояния каждого выпадающего списка

  useEffect(() => {
    if (tags) {
      setFilteredTags(tags.filter(tag => tag.count !== 0));
      if (activeCategory && activeCategory.length > 0) {
        const activeCategoryContent = categories.find(item => item.termId === activeCategory);
        setFilteredTags(tags.filter(tag => activeCategoryContent.category_tags.includes(tag.termId)));
      }
    }
  }, [tags, activeCategory]);

  useEffect(() => {
    setSelectedTags([]);
  }, [activeCategory]);

  useEffect(() => {
    setSelectedTags(activeTags);
  }, []);

  const toggleDropdown = (index) => {
    setDropdownStates(prevState => ({
      ...prevState,
      [index]: !prevState[index]
    }));
  };

  const handleTagToggle = (tagId) => {
    let activeTagsTemporary = [];
    if (selectedTags.includes(tagId)) {
      activeTagsTemporary = selectedTags.filter(selectedTag => selectedTag !== tagId);
    } else {
      activeTagsTemporary = [...selectedTags, tagId];
    }
    setSelectedTags(activeTagsTemporary);
    onTagClick(activeTagsTemporary);
  };

  const handleDocumentClick = (event) => {
    // Закрываем все выпадающие списки при клике вне них
    Object.keys(dropdownStates).forEach((key) => {
      if (dropdownStates[key] && !event.target.closest('.sorting-content')) {
        setDropdownStates(prevState => ({
          ...prevState,
          [key]: false
        }));
      }
    });
  };

  useEffect(() => {
    document.addEventListener("mousedown", handleDocumentClick);
    return () => {
      document.removeEventListener("mousedown", handleDocumentClick);
    };
  }, []);

  return (
    <section className='tag-section'>
      <div className='sorting-area'>
        <div className='sorting-dropdown'>
          <button type='button' className='tag-pseudo-button pointer' onClick={() => toggleDropdown(0)}>Камень <img src={caret} alt="caret" /></button>
          <div className={`sorting-content ${dropdownStates[0] ? '' : 'sorting-content_visible'}`}>
            <div className="tags__dropdown">
              {tags && filteredTags.map(tag => (
                <label key={tag.termId} className='tag-label'>
                  <input
                    type="checkbox"
                    value={tag.termId}
                    className='tag-button_dropdown'
                    checked={selectedTags && selectedTags.includes(tag.termId)}
                    onChange={() => handleTagToggle(tag.termId)}
                  />
                  <div className='tag-pseudo-checkbox pointer'>{tag.name}{!activeCategory && (<span className="tag-count">{tag.count}</span>)} </div>
                </label>
              ))}
            </div>
          </div>
        </div>
        <div className='sorting-dropdown'>
          <button type='button' className='tag-pseudo-button pointer' onClick={() => toggleDropdown(1)}>Сортировка <img src={caret} alt="caret" /></button>
          <div className={`sorting-content ${dropdownStates[1] ? '' : 'sorting-content_visible'}`}>
            <div className="tags__dropdown">
              {tags && filteredTags.map(tag => (
                <label key={tag.termId} className='tag-label'>
                  <input
                    type="checkbox"
                    value={tag.termId}
                    className='tag-button_dropdown'
                    checked={selectedTags && selectedTags.includes(tag.termId)}
                    onChange={() => handleTagToggle(tag.termId)}
                  />
                  <div className='tag-pseudo-checkbox pointer'>{tag.name}{!activeCategory && (<span className="tag-count">{tag.count}</span>)} </div>
                </label>
              ))}
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};



