import './Profile.css';
import Header from '../Header/Header';
import productsApi from '../../utils/Api';

export default function Orders({ orders }) {

  console.log(orders)
  if (!orders) {
    return <div>Loading...</div>
  }

  function getDate(timestamp) {
    return new Date(timestamp * 1000).toLocaleDateString('ru-RU');
  }


  return (
    <>
    <Header/>
    <main className='orders'>
      <ul className='orders-list'>
        <h1>Заказы</h1>
        {orders.map((order) => (
          <li className='order'>
            <div className='flex_type_row order__head'>
              <h3 className='order__header'>Заказ от {getDate(order.createdAt)}</h3>
              <p className='order__price'>{order.totalPrice} &#8381;</p>
            </div>
            <div className='flex_type_row'>
              <div className=''>
                <p className='order__number'>№{order.orderId}</p>
                <p className='order__status'>{order.orderStatus = 0 ? "Создан" : ""}</p>
              </div>
              <div className='order__products'>
              {order.orderedProducts.map((product) => (
                <a href={`https://dolina.shop/product/${product.slug}`}>
                  <img className='order__pic' alt={product.title} src={`https://dolina.shop/photos/${product.path.slice(0, -4) + `_preview.jpg`}`}></img>
                </a>
              ))}
            </div>
            </div>
            
            

    
          </li>
        ))}
      </ul>
    </main>
    </>
  )
}

