import starIcon from '../../images/star.svg';
import starActiveIcon from '../../images/star-fill.svg';
import React, { useState, useEffect } from 'react';
// import Header from '../Header/Header';
import AdminHeader from './AdminHeader';
import './ProductList.css';
import FileUploader from './FileUploader';
import adminApi from '../../utils/adminApi';
import productsApi from '../../utils/Api';
import { DEFAULT_COLOR_LIST, FEATURES_LIST, HARDCODED_CATEGORIES } from '../../utils/constants';

import imageIcon from '../../images/image.svg';
import copySimple from '../../images/copy-simple.svg';


function ProductList() {

    const [products, setProducts] = useState([]);

    const [currentProductId, setCurrentProductId] = useState([]); // for price editing etc.
    const [price, setPrice] = useState(0);
    const [salePrice, setSalePrice] = useState(0);
    const [discount, setDiscount] = useState(0);
    const [priceBlockStyle, setPriceBlockStyle] = useState({ display: "none" });

    const [offset, setOffset] = useState(0);
    const [loading, setLoading] = useState(false);
    const pageSize = 50;

    const fetchProducts = async () => {
        setLoading(true);
        try {
            const data = await productsApi.filterProducts('', '', '', offset, pageSize);
            setProducts(prevProducts => [...prevProducts, ...data]);
            setOffset(prevOffset => prevOffset + pageSize);
        } catch (error) {
            console.error('Error fetching products', error);
        }
        setLoading(false);
    };

    useEffect(() => {
        // console.log('rendered')
        fetchProducts();
    }, []); // Fetch initial products on mount


    function toggleStar(event, product) {
        const starState = product.star == 1 ? 0 : 1;
        const starButton = event.target;

        starButton.style.opacity = 0.3;

        adminApi.setStar(product.productId, starState)
            .then(response => {
                setProducts(prevProducts =>
                    prevProducts.map(p =>
                        p.productId === product.productId
                            ? { ...p, star: starState }
                            : p
                    )
                )

                starButton.style.opacity = 1;
            }
            );
    }

    function handlePriceClick(event, productId, price, salePrice, discount) {

        const target = event.target;
        const rect = target.getBoundingClientRect();

        setCurrentProductId(productId)
        setPrice(price);
        setSalePrice(salePrice);
        setDiscount(discount ?? '0');

        const top = rect.top + window.scrollY - 110 + (rect.height / 2);

        setPriceBlockStyle({
            display: 'block',
            top: `${top}px`
        });
    }

    useEffect(() => {
        if (price > 0 && discount > 0) {
            setSalePrice(Math.floor((price - (price / 100 * discount)) / 10) * 10);
        } else {
            setSalePrice('');
        }
    }, [discount, price]);

    function closePricePopup() {
        setPriceBlockStyle({ display: 'none' });
    }

    function savePricePopup() {
        setPriceBlockStyle({ display: 'none' });
        const priceElement = document.querySelector('.table_price.p_' + currentProductId);
        priceElement.style.opacity = 0.25;

        adminApi.updatePrice(currentProductId, price, salePrice, discount)
            .then(response => {
                priceElement.style.opacity = 1;
                setProducts(prevProducts =>
                    prevProducts.map(p =>
                        p.productId === currentProductId
                            ? { ...p, price: price, salePrice: salePrice, discount: discount }
                            : p
                    )
                )
            }
            );
    }

    function handleInStockChange(productId, newInStock) {
        const inStockElement = document.querySelector('.table_instock.p_' + productId);
        inStockElement.style.opacity = 0.25;

        adminApi.setInStock(productId, newInStock)
            .then(response => {
                inStockElement.style.opacity = 1;
                setProducts(prevProducts =>
                    prevProducts.map(p =>
                        p.productId === productId
                            ? { ...p, inStock: newInStock }
                            : p
                    )
                )

                // starButton.style.opacity = 1;
            }
            );
    }

    return (
        <>
            <div className='admin_page product_list_page'>
                <AdminHeader
                />
                <div className="productList">
                    <div className="table_header">
                        <span className="product_link_title"><img src={imageIcon} />Название</span>
                        <span>Артикул</span>
                        <span>Цена</span>
                        <span>Наличие</span>
                        <span>Категории</span>
                        <span>Камни</span>
                        <span className='justify_center'><img src={starIcon} /></span>
                        <span>Статус</span>
                        {/* <span>Редактировать</span> */}
                        <span>Копировать</span>
                    </div>
                    {products.map(product => (
                        <div className='table_product' key={product.productId}>
                            <a href={'/administration/product/' + product.productId} title='Редактировать товар' className='product_link'><span>
                                <img src={"https://dolina.shop/photos/" + product.pictures[0].path.slice(0, -4) + `_preview.jpg`} alt={product.title} className='table_product_img' />
                            </span>
                                <span>{product.title}</span></a>
                            <span>{product.SKU}</span>
                            <span className={`table_price p_${product.productId}`}
                                onClick={(event) => handlePriceClick(event, product.productId, product.price, product.salePrice, product.discount)}
                            ><span className={`${product.salePrice ? 'line-through opacity-medium' : ''}`}>{product.price} ₽</span>{product.salePrice > 0 && <span className='table_sale_price'>{product.salePrice} ₽</span>}</span>

                            <span className={`table_instock p_${product.productId} justify_center`} >
                                <div className='instock_buttons instock_minus unselectable' onClick={() => product.inStock > 0 && handleInStockChange(product.productId, product.inStock - 1)}>–</div>
                                {product.inStock}
                                <div className='instock_buttons instock_plus unselectable' onClick={() => handleInStockChange(product.productId, product.inStock + 1)}>+</div></span>
                            <span className='table_categories'>{product.categories.slice(0, 2).map(category => category.name).join(', ')}{product.categories.length > 2 && '...'}</span>
                            <span className='table_tags'>{product.tags.slice(0, 2).map(tag => tag.name).join(', ')}{product.tags.length > 2 && '...'}</span>
                            <span className='starred justify_center' onClick={(event) => toggleStar(event, product)}>{product.star ? <img src={starActiveIcon} /> : <img src={starIcon} />}</span>
                            <span className='table_published'>{product.published ? 'Опубликовано' : 'Черновик'}</span>
                            {/* <span><a href={'/administration/product/' + product.productId}>Изменить</a></span> */}
                            {/* <span className='deleteProduct' onClick={() => deleteProduct(product.productId)}> [x]</span> */}
                            <span className='table_copy'><a href={'/administration/product/' + product.productId + '/copy'}><img src={copySimple} /> Копировать</a></span>
                        </div>
                    ))}

                    <div className='price_block block' style={priceBlockStyle}>
                        <label htmlFor='input-price' className={`full_price ${discount === 0 ? 'font-weight-600' : ''}`}>Полная цена</label>
                        <input className={`price_input ${discount > 0 ? 'line-through opacity-medium' : ''}`} type="number" placeholder='' id='input-price' value={price} onChange={(e) => setPrice(e.target.value)} />
                        <div className={`price_ruble_icon ${discount > 0 ? 'opacity-medium' : ''}`}>₽</div>

                        <label htmlFor='input-sale-price' className={`sale_price ${discount > 0 ? 'font-weight-600' : 'opacity-medium'}`}>Цена по скидке</label>
                        <input className={`sale_price_display ${discount === 0 || price < 1 ? 'opacity-low' : ''}`} type="number" id='input-sale-price' value={salePrice ?? ''} onChange={(e) => setSalePrice(e.target.value)} disabled /><div className={`sale_price_ruble ${discount === 0 ? 'opacity-low' : ''}`}>₽</div>

                        <label htmlFor='input-discount' className={`discount_text ${price < 1 ? 'opacity-low' : ''}`}>Скидка</label>
                        <input className='discount_slider' type="range" min="0" max="50" placeholder="0" id='input-discount' value={discount ?? 0} onChange={(e) => setDiscount(e.target.value)} disabled={price < 1} />
                        <div className={`discount_percentage ${price < 1 ? 'opacity-low' : ''}`}>{discount}%</div>

                        <div className='list_close_price list_price_buttons' onClick={closePricePopup}>Закрыть</div>
                        <div className='list_save_price list_price_buttons' onClick={savePricePopup}>Сохранить</div>
                    </div>
                </div>

                 <div className='load_more_button'>
                <button onClick={() => fetchProducts()} disabled={loading}>
                    {loading ? 'грузится...' : 'загрузить ещё'}
                </button></div>

            </div>
        </>
    )
};

export default ProductList;
