import MobileNavBar from "../MobileNavBar/MobileNavBar";
import './Pages.css';
import { useEffect } from "react";
import sparkle from '../../images/sparkle-success.svg';
import uni from '../../images/uni2.png';
import logo from '../../images/logo_pic.png';
import mount from '../../images/urals.jpg';
import mount2 from '../../images/urals2.jpg';


export default function AboutPage({ onBurgerClick, onBurgerClose, isBurgerOpen, orders, cart }) {
  useEffect(() => {
    document.title = 'О нас - Долина самоцветов';
  }, []);

  return (
    <>
      <main className="about">
        <div className="about__container_1">
          <div>
          <h1 className="about__header">Добро пожаловать в Долину Самоцветов!</h1>
            <p className="about__subheader">У нас вы найдёте украшения с натуральными камнями, от элегантно-скромных и минималистичных до необычных, редких и по-настоящему уникальных.</p>
            <p className="about__maintext">
              Весь наш небольшой, но дружный коллектив крепко объединяет любовь к натуральному камню. Многие из нас по образованию геологи: закончили Свердловский горный институт
              им. Вахрушева (ныне УГГУ-Уральский государственный горный университет). Работали по специальности на месторождениях Казахстана, Якутии и России, в том числе на родном Урале.
              И вот уже более 20 лет занимаемся изделиями из природного камня.
            </p></div>
          <div className="about__masonry">

            <img className="about__masonry-item" src={uni} alt='УГГУ'></img>
            <img className="about__masonry-item" src={mount} alt='Уральские горы'></img>


            <img className="about__masonry-item about__masonry-item_logo" alt='Логотип с серебряным копытцем' src={logo}></img>
            <img className="about__masonry-item" src={mount2}></img>
          </div>
        </div>


      </main>
      <MobileNavBar
        onBurger={onBurgerClick}
        isBurgerOpen={isBurgerOpen}
        onBurgerClose={onBurgerClose}
        cart={cart}
        orders={orders}
      />
    </>
  )
}
