import './Tags.css';
import { useEffect, useState } from 'react';
import SearchForm from '../SearchForm/SearchForm';
import caret from '../../images/caret-down_mini.svg';

export default function Tags({ tags, categories, onTagClick, activeCategory, activeTags }) {
    const [selectedTags, setSelectedTags] = useState([]);
    const [filteredTags, setFilteredTags] = useState([]);
    const [openDropdownIndex, setOpenDropdownIndex] = useState(null);

    const handleTagToggle = (tag) => {
        let activeTagsTemporary = [];
        if (selectedTags.includes(tag)) {
            activeTagsTemporary = selectedTags.filter(selectedTag => selectedTag !== tag)
        } else {
            activeTagsTemporary = [...selectedTags, tag];
        }
        setSelectedTags(activeTagsTemporary);
        onTagClick(activeTagsTemporary);
    };

    const toggleDropdown = (index) => {
        setOpenDropdownIndex(prevIndex => (prevIndex === index ? null : index)); // Открываем или закрываем текущий выпадающий список
    };

    useEffect(() => {
        if (tags) {
            setFilteredTags(tags.filter(tag => tag.count !== 0));
            if (activeCategory && activeCategory.length > 0) {
                const activeCategoryContent = categories.filter(item => item.termId == activeCategory)[0];
                setFilteredTags(tags.filter(tag => activeCategoryContent.category_tags.includes(tag.termId)));
            }
        }
    }, [tags, activeCategory]);

    useEffect(() => {
        setSelectedTags([]);
    }, [activeCategory]);

    useEffect(() => {
        setSelectedTags(activeTags);
    }, []);

    useEffect(() => {
      const handleOutsideClick = (event) => {
          // Получаем элемент выпадающего списка
          const dropdowns = document.querySelector('.sorting-dropdown');
  
          // Проверяем, был ли клик выполнен вне области выпадающего списка
          if (dropdowns && dropdowns.length > 0) {
              dropdowns.forEach(dropdown => {
                  if (!dropdown.contains(event.target)) {
                      setOpenDropdownIndex(null); // Закрываем выпадающий список
                  }
              });
          }
      };
  
      // Добавляем обработчик события клика на документ
      document.addEventListener('click', handleOutsideClick);
  
      // Очищаем обработчик при размонтировании компонента
      return () => {
          document.removeEventListener('click', handleOutsideClick);
      };
  }, []);

    return (
        <section className='tag-section'>
            <div className='sorting-area'>
            <div className='sorting-dropdown'>
                    <button type='button' className='tag-pseudo-button pointer' onClick={() => toggleDropdown(0)}>Сортировка <img src={caret} alt="caret" /></button>
                    <div className={`sorting-content ${openDropdownIndex === 0 ? 'sorting-content_visible' : ''}`}>
                        <div className="tags__dropdown">
                            
                        </div>
                    </div>
                </div>
                <div className='sorting-dropdown'>
                    <button type='button' className='tag-pseudo-button pointer' onClick={() => toggleDropdown(3)}>Особенности <img src={caret} alt="caret" /></button>
                    <div className={`sorting-content ${openDropdownIndex === 3 ? 'sorting-content_visible' : ''}`}>
                        <div className="tags__dropdown">
                            {tags && filteredTags.map(tag => (
                                <label key={tag.termId} className='tag-label'>
                                    <input
                                        type="checkbox"
                                        value={tag.termId}
                                        className='tag-button_dropdown'
                                        checked={selectedTags && selectedTags.some(selectedTag => selectedTag === tag.termId) ? 'checked' : ''}
                                        onChange={() => handleTagToggle(tag.termId)}
                                    />
                                    <div className='tag-pseudo-checkbox pointer'>{tag.name}{!activeCategory && (<span className="tag-count">{tag.count}</span>)} </div>
                                </label>
                            ))}
                        </div>
                    </div>
                </div>
                <div className='sorting-dropdown'>
                    <button type='button' className='tag-pseudo-button pointer' onClick={() => toggleDropdown(5)}>Камень <img src={caret} alt="caret" /></button>
                    <div className={`sorting-content ${openDropdownIndex === 5 ? 'sorting-content_visible' : ''}`}>
                        <div className="tags__dropdown">
                            <SearchForm />
                            {tags && filteredTags.map(tag => (
                                <label key={tag.termId} className='tag-label'>
                                    <input
                                        type="checkbox"
                                        value={tag.termId}
                                        className='tag-button_dropdown'
                                        checked={selectedTags && selectedTags.some(selectedTag => selectedTag === tag.termId) ? 'checked' : ''}
                                        onChange={() => handleTagToggle(tag.termId)}
                                    />
                                    <div className='tag-pseudo-checkbox pointer'>{tag.name}{!activeCategory && (<span className="tag-count">{tag.count}</span>)} </div>
                                </label>
                            ))}
                        </div>
                    </div>
                </div>
            </div>
        </section>
    );
};

