import './OneStepPopup.css';
import { useEffect, useState, useContext } from "react";
import { CurrentUserContext } from '../../../contexts/CurrentUserContext';
import usersApi from '../../../utils/UsersApi';

export default function OneStepPopup({ productId }) {

  const currentUser = useContext(CurrentUserContext);
  console.log(currentUser.userId);
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  // const [email, setEmail] = useState("");
  const [comment, setcomment] = useState("");
  const [cart, setCart] = useState({});

function handleOrderSubmit(e) {
  e.preventDefault();
  const orderData = {
    name: name,
    phone: phone,
    email: currentUser.email,
    comment: comment, 
    userId: currentUser.userId,
    cart: currentUser.cart,
    productId: productId,
  }
  console.log(orderData)
  usersApi.addQuickOrder(orderData)
  .then((res) => console.log(res))
}


useEffect(() => {
  if (!currentUser.cart) {
    console.log('блин')
    const localCart = JSON.parse(localStorage.getItem('cart'));
    setCart(localCart)
  }
  setCart(currentUser.cart)
  console.log(cart)
}, [currentUser])

  return (
    <div className='onestep-popup'>
      <div className='onestep__overlay'>
        <div className='onestep__content'>
          <button className="onestep__close-button">
          </button>
          <h2>Заказ в 1 клик</h2>
          <form className='onestep__form' onSubmit={handleOrderSubmit}>
            <div className='onestep__form-flex'>
              <label htmlFor='phone'>
                Телефон*
              </label>
              <input className='onestep__input'
                type="tel"
                placeholder='Нужен для оформления заказа'
                name='phone'
                value={phone || ''}
                onChange={(e) => setPhone(e.target.value)}
                required
                minLength={10}
                maxLength={12}
              />
              {/* <p className='input__error'>{nameValidationError}</p> */}
            </div>
            {/* <div className='onestep__form-flex'>
              <label htmlFor='email'>
                Email
              </label>
              <input className='onestep__input'
                type="email"
                name='email'
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              // required
              />
            </div> */}
            <div className='onestep__form-flex'>
              <label htmlFor='name'>
                Имя
              </label>
              <input className='onestep__input'
                type="text"
                name='name'
                value={name || ''}
                placeholder='Как к Вам обращаться?'
                onChange={(e) => setName(e.target.value)}
                required
                minLength={3}
                maxLength={20}
              />
            </div>
            <div className='onestep__form-flex'>
              <label>
                Дополнительная информация
              </label>
              <textarea className='onestep__textarea'
                value={comment || ''}
                onChange={(e) => setcomment(e.target.value)}
                // required
                placeholder='Например, Ваш город, email'
              />
            </div>
            <button className='onestep__submit-button' type="submit">Заказать</button>
          </form>
        </div>
      </div>
    </div>
  )
}
