import { useEffect, useState } from 'react';
import './App.css';
import MainPage from './pages/MainPage';
import CatalogPage from './pages/CatalogPage';
import ProductPage from './pages/ProductPage';
import RegisterForm from './Form/RegisterForm';
import LoginForm from './Form/LoginForm';
import ShoppingCart from './ShoppingCart/ShoppingCart';
// import MobileNavBar from './MobileNavBar/MobileNavBar';
import Products from './Catalog/Catalog';
// import Crumbs from './Crumbs/Crumbs';
import Product from './Product/Product';
import productsApi from '../utils/Api';
import usersApi from '../utils/UsersApi';

import { Route, Routes, useNavigate, NavLink } from 'react-router-dom';

function App() {

  const navigate = useNavigate();
  const [productData, setProductData] = useState([]);
  const [products, setProducts] = useState([]);
  const [inputError, setInputError] = useState('');

  function handleLogin(email, password) {
    setInputError('');
    usersApi.login(email, password)
      // .then(() => {
      //   setIsLoggedIn(true);
      //   navigate('/movies', { replace: true })
      // })
      .catch((err) => {
        if (err.status === 401) {
          setInputError('Неправильный логин или пароль')
        } else {
          setInputError('При авторизации произошла ошибка')
        }
      })
  }


  function handleRegistration(name, email, password) {
    setInputError('');
    usersApi.register(name, email, password)
      // .then(() => {
      //   handleLogin(email, password);
      // })
      .catch((err) => {
        if (err.status === 409) {
          setInputError('Пользователь с таким email уже существует')
        } else {
          setInputError('При регистрации пользователя произошла ошибка')
        }
      })
  }



  useEffect(() => {
    productsApi.getAllProducts()
      .then((data) => {
        // console.log(data);
        setProducts(data);
      }).catch((err) => {
        // console.log(err);
      })
  }, []) // когда в скобках products, запросы идут бесконечно. Почему?

  function handleCardClick(id) { // откуда id? не помню
    // console.log(id);
    productsApi.getProduct(id)
      .then((data) => setProductData(data))
      .then(() => {
        navigate('/product', { replace: true })
      })
  }


  return (
    <div className="page">
            <div className='header_block_1'>
        <div className='header__container'>
          <NavLink to='/' className='navlink_size_s color_white' >О доставке</NavLink>
          <NavLink to='/' className='navlink_size_s color_white' >О нас</NavLink>

        </div>
      </div>
      <div className='content'>
        <Routes>
        <Route path="/" element={<MainPage
              onCardClick={handleCardClick}
              products={products}
            />} />
            <Route path="/c" element={<CatalogPage
              onCardClick={handleCardClick}
              products={products}
            />} />
            <Route path="/product" element={<ProductPage
              productData={productData}
            />} />
            <Route path="/signup" element={<RegisterForm
              onRegistration={handleRegistration}
              inputError={inputError}

            />} />
            <Route path="/signin" element={<LoginForm
            />} />
            <Route path="/cart" element={<ShoppingCart
              products={products}
              onCardClick={handleCardClick}
            />} />
        </Routes>
      </div>
    </div>
  )
}

export default App;

// Страницы: 
// - главная
