import './Profile.css';
import Header from '../Header/Header';
import productsApi from '../../utils/Api';

export default function Orders({ orders }) {

  console.log(orders)
  if (!orders) {
    return <div>Loading...</div>
  }

  // function getProduct(id) {
  //   productsApi.getProduct(id)
  // }

  function getDate(timestamp) {
    const date = new Date(timestamp * 1000);
    const day = date.getDate(); // День месяца (от 1 до 31)
    const month = date.getMonth() + 1; // Месяц (от 0 до 11, поэтому прибавляем 1)
    const year = date.getFullYear(); // Год
    return `${day}.${month}.${year}`
  }


  return (
    <>
    <Header/>
    <main className='orders'>
      <ul className='orders-list'>
        <h1>Заказы</h1>
        {orders.map((order) => (
          <li className='order'>
            <h3 className='order__header'>Заказ от {getDate(order.createdAt)} на {order.totalPrice} &#8381;</h3>
            <div className=''>
              <p className='order__number'>№{order.orderId}</p>
              <p className='order__status'>{order.orderStatus}</p>
            </div>
            <div className='order__products'>
              {/* {order.orderedProducts.map((product) => (
                
              ))} */}
            </div>

    
          </li>
        ))}
      </ul>
    </main>
    </>
  )
}
