import {React, useState, useEffect }from "react";
import { CurrentUserContext } from '../../contexts/CurrentUserContext';

export default function ProfilePopup({ isOpen, onClose, onUpdateUser, onSignout }) {
  const currentUser = React.useContext(CurrentUserContext);
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');

  useEffect(() => {
    setName(currentUser.name);
    setEmail(currentUser.email);
  }, [currentUser, isOpen]);
  return (
   <div className={`profile-popup ${isOpen ? `popup_opened` : ""}`}>
    <section className='form__container'>
            <form name='profile-form' onSubmit={onUpdateUser}>
              <fieldset className="form__fieldset">
                <h1 className="form__title">ы</h1>
               
                <button type="submit" 
                name="submit" 
                className="form__submit-button hover_type_normal" 
                // disabled={submitDisabled}
                >Сохранить</button>
              </fieldset>
            </form>
          </section>
          <button type="button" className="profile-popup__close-button" onClick={onClose}></button>
   </div>
  )

}
