import './OneStepPopup.css';
import { useEffect, useState, useContext } from "react";
// import closeIcon from '../../../images/';

export default function OneStepPopup() {

  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");

  return (
    <div className='onestep-popup'>
      <div className='onestep__overlay'>
        <div className='onestep__content'>
          <button className="onestep__close-button">
          </button>
          <h2>Заказ в 1 клик</h2>
          <form className='onestep__form'>
            <div className='onestep__form-flex'>
              <label htmlFor='phone'>
                Телефон*
              </label>
              <input className='onestep__input'
                type="phone"
                name='phone'
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>
            {/* <div className='onestep__form-flex'>
              <label htmlFor='email'>
                Email
              </label>
              <input className='onestep__input'
                type="email"
                name='email'
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              // required
              />
            </div> */}
            <div className='onestep__form-flex'>
              <label htmlFor='name'>
                Имя
              </label>
              <input className='onestep__input'
                type="text"
                name='name'
                value={name}
                placeholder='Как к Вам обращаться?'
                onChange={(e) => setName(e.target.value)}
                required
              />
            </div>
            <div className='onestep__form-flex'>
              <label>
                Примечание
              </label>
              <textarea className='onestep__input'
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                required
              />
            </div>
            <button className='onestep__submit-button' type="submit">Заказать</button>
          </form>
        </div>
      </div>
    </div>
  )
}
