import './OneStepPopup.css';
import { useEffect, useState, useContext } from "react";
// import closeIcon from '../../../images/';

export default function OneStepPopup() {

  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");

  return (
    <div className='onestep-popup'>
      <div className='onestep__overlay'>
        <div className='onestep__content'>
          <button className="onestep__close-button">
          </button>
          <h2>Заказ в 1 клик</h2>
          <form className='onestep__form'>
            <label htmlFor='name'>
              Имя:
            </label>
            <input
              type="text"
              name='name'
              value={name}
              placeholder='Как к Вам обращаться?'
              onChange={(e) => setName(e.target.value)}
              required
            />
            <label htmlFor='phone'>
              Телефон:
            </label>
            <input
              type="phone"
              name='phone'
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
            <label htmlFor='email'>
              Email:
            </label>
            <input
              type="email"
              name='email'
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              // required
            />
            <label>
              Сообщение:
            </label>
            <textarea
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              required
            />
            <button type="submit">Отправить</button>
          </form>
        </div>
      </div>
    </div>
  )
}
