import './Product.css';
import { useState, useEffect } from 'react';
import { useMediaQuery } from 'react-responsive';
import crystalIcon from '../../images/crystal-icon.svg';
import tagIcon from '../../images/tag.svg';
import GalleryPopup from './GalleryPopup/GalleryPopup';
import MobileGallery from './MobileGallery';
import Card from '../Card/Card';
import Catalog from '../Catalog/Catalog';
import productsApi from '../../utils/Api';
import UpsellCard from './UpsellCard';
import emptyBack from '../../images/back.png';

export default function Product({ productData, onLikeClick, savedProducts, onAddClick, cart, onCardClick, salePrice }) {

  const isBigScreen = useMediaQuery({ minWidth: 768 }); // надо бы перенести в константы
  const isSmallScreen = useMediaQuery({ maxWidth: 767 });
  let pictures = productData.pictures;
  let mainPicture = `https://dolina.shop/photos/${pictures[0].path}`; // 
  const ringSizes = productData.ringSizes;
  const previewContainer = document.querySelector('.gallery__previews');

  // const [mainPicture, setMainPicture] = useState(emptyBack)
  const [isPreview, setIsPreview] = useState(emptyBack);
  const [isGalleryPopupOpen, setisGalleryPopupOpen] = useState(false);
  const [selectedSize, setSelectedSize] = useState('1');
  const [isAdded, setIsAdded] = useState(false);
  const [isSaved, setIsSaved] = useState(false);
  const [isUpsells, setUpsells] = useState([]);
  const [isInStock, setisInStock] = useState((productData.inStock > 0 ? true : false));
  const stoneTags = productData.tags.map((tag => tag.name));
  const stones = stoneTags.join(', ');
  // console.log(isAdded)
  useEffect(() => {
    document.documentElement.style.setProperty('--product-color', productData.color);

    pictures = productData.pictures;
    mainPicture = `https://dolina.shop/photos/${pictures[0].path}`; // 
    setIsPreview(mainPicture);
    // setIsPreview(mainPicture);
    // return () => {
    //   setMainPicture(`https://dolina.shop/photos/${pictures[0].path}`);
    //   setIsPreview(mainPicture);
    // };

    productsApi.getProductsById([productData.upsells])
      .then((res) => {
        setUpsells(res)
        // console.log(upsells)
      })
      .catch((err) => console.log(err))

  }, [productData]);

  useEffect(() => {
    if (cart) { // НАКОЛХОЗИЛА ПО-БЫСТРОМУ, ПОТОМУ ЧТО В ПУСТОЙ КОРЗИНЕ ВЫХОДИЛА ОШИБКА
      if (savedProducts.includes(productData.productId)) {
        setIsSaved(true);
        // console.log(isAdded)
      } else {
        setIsSaved(false);
      }
    }
  }, [isSaved, savedProducts, productData])

  useEffect(() => {
    const productIds = Object.keys(cart);
    if (productIds.includes(productData.productId.toString())) {
      setIsAdded(true);
    }
  }, [isAdded, cart, productData])

  function handleImageClick() {
    setisGalleryPopupOpen(true);
  }

  function handleCloseClick() {
    setisGalleryPopupOpen(false);
  }

  function handleLike() {
    onLikeClick(productData.productId)
  }

  function scrollUp() {
    if (previewContainer) {
      previewContainer.scrollTop = 0;
    }
  }

  function scrollDown() {
    if (previewContainer) {
      previewContainer.scrollTop = previewContainer.scrollHeight;
    }
  }

  function handleSizeChange(e) {
    setSelectedSize(e.target.value);
  };

  function handleAdd(e) {
    e.preventDefault();
    const selectedSizeToSend = ringSizes.length === 1 ? ringSizes[0].name : selectedSize;
    // console.log('Selected size to send:', selectedSizeToSend);
    onAddClick(productData.productId, 1, selectedSizeToSend);
    setIsAdded(true);
  }

  console.log(productData)

  return (

    <main className='product'>
      <div className='product__container'>

        {isBigScreen && (
          <div className='gallery'>
            <div className='gallery__previews'>
              <button type='button' className='product__nav-button product__up-button pointer' onClick={scrollUp} />
              {pictures.map((picture) => (
                <div className='gallery__preview-container' key={pictures.indexOf(picture)}>
                  {/* <div className='card__image-overlay'></div> */}
                  <img className={`gallery__preview ${(pictures.indexOf(picture) === 0) && 'gallery_active-border'}`} alt={productData.title} src={`https://dolina.shop/photos/${picture.path.slice(0, -4) + `_preview.jpg`}`} onClick={(evt) => {
                    setIsPreview(`https://dolina.shop/photos/${picture.path}`);
                    document.querySelector('.gallery_active-border').classList.remove('gallery_active-border');
                    evt.target.classList.add('gallery_active-border');
                  }}></img>
                </div>
              ))}
              <button type='button' className='product__nav-button product__down-button pointer' onClick={scrollDown} />

            </div>
            <div className='gallery__container' onClick={handleImageClick}>
              <div className='card__image-overlay'></div>
              <img className='gallery__main-pic' src={isPreview} alt={productData.title}></img>
            </div>
          </div>
        )}

        {isSmallScreen && (
          <div className='gallery_mobile' style={{ color: productData.color }}>
            <MobileGallery
              pictures={pictures}
              color={productData.color}
              onImageClick={handleImageClick} />
            <button type='button' className={(isSaved ? 'card__save-button card__save-button_active' : 'card__save-button')} onClick={handleLike}></button>
            <div className='card__image-overlay'></div>
          </div>
        )}


        <div className='product_block_2'>

          <section className='main-info_mobile'>
            <h1 className='product__title'>{productData.title}</h1>
            {!salePrice > 0 ?
                <span className='product__price'>{productData.price} &#8381;</span>
                : <div className='sale-container'>
                  <span className='product__price product__price_crossed'>{productData.price} &#8381;</span>
                  <span className='product__sale-price'>{salePrice} &#8381;</span>
                </div>}
            {/* <span className='product__price'>{productData.price} &#8381;</span> */}
          </section>

          {ringSizes.length > 0 ? (
            ringSizes.length > 1 ? (
              <section className='product__sizes-container'>
                <h2 className='product__subtitle'>Выберите размер:</h2>
                <ul className='sizes'>
                  {ringSizes.map((size) => (
                    <li className='size-card' key={size.name}>
                      <label htmlFor={size.name} className='size-label'>
                        <input className='size-button' type='radio' name='size' value={size.name} id={size.name} onChange={handleSizeChange} />
                        <div className='size-pseudo-button pointer'>{size.name}</div>
                      </label>
                    </li>
                  ))}
                </ul>
              </section>
            ) : (
              <section className='product__sizes-container'>
                <h2 className='product__subtitle'>Размер:</h2>
                <ul className='sizes'>
                  <li className='size-card'>
                    <label htmlFor={ringSizes[0].name} className='size-label'>
                      <input className='size-button' type='radio' name='size' value={ringSizes[0].name} id={ringSizes[0].name} checked />
                      <div className='size-pseudo-button pointer'>{ringSizes[0].name}</div>
                    </label>
                  </li>
                </ul>
              </section>
            )
          ) : null}

          <section className='product__chars-container'>
            <h2 className='product__subtitle'>Характеристики</h2>

            <table className='table'>
              <tbody className='product_font_main'>

                {productData.tags && <tr className='table__unit'>
                  <th className='table_column_left'>Камень</th>
                  <div></div>
                  <td className='table_column_right'>{stones}</td>
                </tr>}

                {productData.metalType && <tr className='table__unit'>
                  <th className='table_column_left'>Металл</th>
                  <div></div>
                  <td className='table_column_right'>{productData.metalType}</td>
                </tr>
                }
                {productData. weight && <tr className='table__unit'>
                  <th className='table_column_left'>Вес</th>
                  <div></div>
                  <td className='table_column_right'>{productData.weight}</td>
                </tr>}

               {productData.length && <tr className='table__unit'>
                  <th className='table_column_left'>Длина изделия</th>
                  <div></div>
                  <td className='table_column_right'>{productData.length}</td>
                </tr>}

                {productData.stoneSize && <tr className='table__unit'>
                  <th className='table_column_left'>Размер камня</th>
                  <div></div>
                  <td className='table_column_right'>{productData.stoneSize}</td>
                </tr>}

                {productData.stoneOrigin && <tr className='table__unit'>
                  <th className='table_column_left'>Происхождение камня</th>
                  <div></div>
                  <td className='table_column_right'>{productData.stoneOrigin}</td>
                </tr>}

                <tr className='table__unit'>
                  <th className='table_column_left'>Производство</th>
                  <div></div>
                  <td className='table_column_right'>{productData.origin || 'Россия'}</td>
                </tr>
              </tbody>
            </table>

          </section>
          {isSmallScreen &&
            (<section className='product__description-container'>
              <h2 className='product__subtitle'>Описание</h2>
              <p className='product__description product_font_main'>{productData.text}</p>
            </section>)
          }
        </div>

        <div className='product_block_3'>
          <div className=''>
            <form name='product-form' className='product-form'>
              <div className='product-form__heading'>
                <h1 className='product__title'>{productData.title}</h1>
                <button type='button' className={(isSaved ? 'card__save-button card__save-button_active' : 'card__save-button')} onClick={handleLike}></button>
              </div>

              {/* <span className='product__price'>{productData.price} &#8381;</span> */}

              {!salePrice > 0 ?
                <span className='product__price'>{productData.price} &#8381;</span>
                : <div className='sale-container'>
                  <span className='product__price product__price_crossed'>{productData.price} &#8381;</span>
                  <span className='product__sale-price'>{salePrice} &#8381;</span>
                </div>}


              {/* <span className=''>В наличии {productData.inStock} шт.</span> */}

              {isInStock ? (<span className=''>В наличии {productData.inStock} шт.</span>) : (<span className=''>Товара нет в наличии</span>)}

              {/* <button type='submit' className={(isAdded ? 'add-button add-button_added hover_type_normal pointer' : 'add-button hover_type_normal pointer')} onClick={handleAdd}>Добавить в корзину</button> */}

              {isInStock ? (!isAdded ? (<button type='submit' className='add-button  hover_type_normal pointer' onClick={handleAdd}>Добавить в корзину</button>)
                : (<button type='submit' className='add-button_added hover_type_normal pointer'>В корзине &#x2713;</button>)) : (<button type='submit' className='add-button hover_type_normal pointer' disabled>Нет в наличии</button>)
                // !isAdded ? (<button type='submit' className='add-button  hover_type_normal pointer' onClick={handleAdd}>Добавить в корзину</button>) 
                // :(<button type='submit' className='add-button_added hover_type_normal pointer'>В корзине &#x2713;</button>)
              }

              <ul className='product-form__pros'>
                <li className='product-form__pro'>
                  <img className='product-form__pro-icon' alt="" src={crystalIcon} />
                  <span className='product-form__pro-text' >Натуральный камень</span>
                </li>
                <li className='product-form__pro'>
                  <img className='product-form__pro-icon' src={tagIcon} alt="" />
                  <span className='product-form__pro-text'>Примерка</span>
                </li>
              </ul>
            </form>
            {isSmallScreen && (!isUpsells.length > 0 ? (
              <section className='product__upsells  mobile-margin'>
              </section>
            ) : (
              <section className='product__upsells'>
                <h2 className='product__upsells-header'>Рекомендуем</h2>
                <ul className='product__upsells_ul'>
                  {isUpsells.map((product) => (
                    <UpsellCard
                      id={product.productId}
                      slug={product.slug}
                      name={product.title}
                      price={product.price}
                      key={product.productId}
                      onCardClick={onCardClick}
                      onLikeClick={onLikeClick}
                      pictures={product.pictures}
                      savedProducts={savedProducts}
                    ></UpsellCard>
                  ))}
                </ul>

              </section>
            ))}
            <form className='product-form_mobile'>
              {isInStock ? (!isAdded ? (<button type='submit' style={{ backgroundColor: productData.color }} className='add-button hover_type_normal pointer' onClick={handleAdd}>Добавить в корзину</button>)
                : (<button type='submit' className='add-button_added hover_type_normal pointer' onClick={handleAdd} disabled>В корзине &#x2713;</button>)) : (<button type='submit' className='add-button hover_type_normal pointer' onClick={handleAdd} disabled>Нет в наличии</button>)
              }
              {/* <button type='submit' className='add-button hover_type_normal pointer' onClick={handleAdd}>Добавить в корзину</button> */}
              {/* <button type='submit' className={(isAdded ? 'add-button add-button_added hover_type_normal pointer' : 'add-button hover_type_normal pointer')} onClick={handleAdd}>Добавить в корзину</button> */}
            </form>
          </div>

        </div>
      </div>
      {isBigScreen &&
        (<section className='product__description-container'>
          <h2 className='product__subtitle'>Описание</h2>
          <p className='product__description product_font_main'>{productData.text}</p>
        </section>)
      }
      {isBigScreen && (!isUpsells.length > 0 ? (
        <section className='product__upsells'>
        </section>
      ) : (
        <section className='product__upsells'>
          <h2 className='product__upsells-header'>Рекомендуем</h2>
          <ul className='product__upsells_ul'>
            {isUpsells.map((product) => (
              <UpsellCard
                id={product.productId}
                slug={product.slug}
                name={product.title}
                price={product.price}
                key={product.productId}
                onCardClick={onCardClick}
                onLikeClick={onLikeClick}
                pictures={product.pictures}
                savedProducts={savedProducts}
              ></UpsellCard>
            ))}
          </ul>

        </section>
      ))}

      <GalleryPopup alt={productData.title} images={pictures} isOpen={isGalleryPopupOpen} onClose={handleCloseClick} />
    </main>


  )
}

