import { useEffect, useState } from 'react';
import './App.css';
import MainPage from './pages/MainPage';
import CatalogPage from './pages/CatalogPage';
import ProductPage from './pages/ProductPage';
import RegisterForm from './Form/RegisterForm';
import LoginForm from './Form/LoginForm';
import ShoppingCart from './ShoppingCart/ShoppingCart';
import { CurrentUserContext } from '../contexts/CurrentUserContext';
import Wishlist from './Profile/Wishlist';
import ProtectedRoute from './ProtectedRoute';
import productsApi from '../utils/Api';
import usersApi from '../utils/UsersApi';
import NotFoundPage from './NotFoundPage/NotFoundPage';
import Profile from './Profile/Profile';
import Orders from './Profile/Orders';
import ProductConstructor from './AdminPanel/ProductConstructor';
import { Route, Routes, useNavigate, NavLink, useLocation, useSearchParams } from 'react-router-dom';


function App() {

  const navigate = useNavigate();
  const [productData, setProductData] = useState([]);
  const [products, setProducts] = useState([]);
  const [inputError, setInputError] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState((localStorage.getItem('jwt') ? true : false));
  const [currentUser, setCurrentUser] = useState({});
  const [savedProducts, setSavedproducts] = useState([]);
  const [orders,setOrders] = useState([]);
  const [cart, setCart] = useState([]);
  const [isProfilePopupOpen, setIsProfilePopupOpen] = useState(false);
  const [terms, setTerms] = useState([]);
  // const [inStock, isInStock] = useState(true);
  // const [isSaved, setIssaved] = useState(false);
  const location = useLocation();
  
  // получаем данные при первой загрузке страницы, один раз
  const searchParams = new URLSearchParams(location.search)
  const categoryQuery = searchParams.get('category');
  const tagsQuery = searchParams.get('tags');
  const [category, setCategory] = useState(categoryQuery);
  const [tags, setTags] = useState(tagsQuery ? tagsQuery.split(',').map(id => parseInt(id)) : '');

  function handleLogin(email, password) {
    setInputError('');
    usersApi.login(email, password)
      .then(() => {
        setIsLoggedIn(true);
        navigate('/', { replace: true }) // replace is messing up the history
      })
      .catch((err) => {
        if (err.status === 401) {
          setInputError('Неправильный логин или пароль')
        } else {
          setInputError('При авторизации произошла ошибка')
        }
      })
  }

  // console.log(products)

  function handleRegistration(name, email, password) {
    setInputError('');
    usersApi.register(name, email, password)
      .then(() => {
        handleLogin(email, password);
      })
      .catch((err) => {
        if (err.status === 409) {
          setInputError('Пользователь с таким email уже существует')
        } else {
          setInputError('При регистрации пользователя произошла ошибка')
        }
      })
  }

  function handleSignOut() {
    setIsLoggedIn(false);
    localStorage.removeItem('jwt');
    navigate('/signin', { replace: true }); // replace is messing up the history
  }

  function handleUpdateUser(e) {
    usersApi.updateUserData(e)
    .then((res) => setCurrentUser(res));
    setIsProfilePopupOpen(false);
  }

  function handleChangeClick() {
    // console.log(isProfilePopupOpen)
    setIsProfilePopupOpen(true);
  }

  function handleClosePopupClick() {
    // console.log(isProfilePopupOpen)
    setIsProfilePopupOpen(false)
  }


  // КОРЗИНА
  useEffect(() => {
    const savedСart = localStorage.getItem('cart');
    if (savedСart) {
      setCart(JSON.parse(savedСart));
    }
  }, []);

  function handleAddToCart(productId, checked, size) {
    const newCart = {...cart};
    newCart[productId] = {checked, size}; // это типа как эррэй
    setCart(newCart);
    if (isLoggedIn) {
      // console.log(newCart)
      // патчим в корзину юзера
      usersApi.updateCart(JSON.stringify(newCart))
    } else {
      localStorage.setItem('cart', JSON.stringify(newCart));
    }
  }

  function handleRemoveFromCart(productId) {
    const newCart = {...cart};
    delete newCart[productId];
    setCart(newCart);
    if (isLoggedIn) {
      usersApi.updateCart(JSON.stringify(newCart))
    } else {
      localStorage.setItem('cart', JSON.stringify(newCart));
    }
  }

  // function update(productId, checked, size) {
  //   const newCart = {...cart};
  //   newCart[productId] = {checked, size}; // это типа как эррэй
  //   setCart(newCart);
  //   if (isLoggedIn) {
  //     console.log(newCart)
  //     // патчим в корзину юзера
  //     usersApi.updateCart(JSON.stringify(newCart))
  //   } else {
  //     localStorage.setItem('cart', JSON.stringify(newCart));
  //   }
  // }

  // переход в товар
  function handleCardClick(newSlug) {
    navigate(`/product/${newSlug}`, { replace: true }); // replace is messing up the history
    // history.push ?
  }

  // лайк
  function handleLikeClick(id) {
    if (isLoggedIn) {
      if (savedProducts.includes(id)) {      
        usersApi.removeProduct(id)
        .then(() => {
          setSavedproducts(savedProducts.filter(item => item !== id));
        })
        .catch(err => console.log(err));
      } else {
        usersApi.saveProduct(id)
        .then(() => {
          setSavedproducts([id, ...savedProducts]);
        })
        .catch(err => console.log(err));
      }
    }
    else {
      navigate('/signin', { replace: true }); // replace is messing up the history
    }
  }


  // Аутентификация, ставит current-юзера
  // useEffect(() => {
  //   const token = localStorage.getItem('jwt');
  //   const savedСart = localStorage.getItem('cart');
  //   // console.log(token)
  //   if (token) {
  //     usersApi.checkToken(token)
  //       .then((res) => {
  //         usersApi.getSavedProducts()
  //         .then((wishlist) => {
  //           res.wishlist = wishlist;
