import { useState, useEffect } from "react";
import Catalog from "../Catalog/Catalog";
import productsApi from "../../utils/Api";
import Header from "../Header/Header";
import MobileNavBar from "../MobileNavBar/MobileNavBar";

export default function SavedMovies({ onCardClick, onLikeClick, savedProducts, terms, onCatClick, category, onBurgerClick, onBurgerClose, isBurgerOpen, search, setSearch, orders, cart }) {

  const [wishlist, setWishlist] = useState([])

  // взять массив айдишников и сделать запрос 

  useEffect(() => {
    productsApi.getProductsById([savedProducts])
      .then((res) => {
        setWishlist(res)
        // console.log(res)
      })
      .catch((err) => console.log(err))
  }, [savedProducts])

  // console.log(wishlist)

  return (
    <main style={{ minHeight:'60vh' }}>
      <h1 className="wishlist-header">Избранное</h1>
      {
        savedProducts.length === 0 ? (
          <div>
            <p style={{ margin: '0' }}>Пока в корзину ничего не добавлено</p>
          </div>
        ) : (
          <Catalog
            onCardClick={onCardClick}
            onLikeClick={onLikeClick}
            savedProducts={savedProducts}
            products={wishlist}
          />
        )
      }

      <MobileNavBar
        onBurger={onBurgerClick}
        isBurgerOpen={isBurgerOpen}
        onBurgerClose={onBurgerClose}
        cart={cart}
        orders={orders}
      />
    </main>
  )
}
