import './ShoppingCart.css';
import Header from '../Header/Header';
import CartProduct from './CartProduct/CartProduct';
import { useEffect, useState } from 'react';
import { useMediaQuery } from 'react-responsive';

export default function ShoppingCart({ products, onCardClick }) {

  // Для вёрстки используется всё тот же временный массив продуктов

  const total = products.reduce((accumulator, product) => accumulator + product.price, 0);

  const [isChecked, setIsChecked] = useState(true);
  const isBigScreen = useMediaQuery({ minWidth: 768 }); // надо бы перенести в константы
  const isSmallScreen = useMediaQuery({ maxWidth: 767 });

  function handleCheckboxClick(id) { // не работает
    setIsChecked(false)
    console.log(isChecked)
  }

  return (
    <>
    <Header/>
    <main className='cart flex_type_column'>
    <h1 className='cart__header'>Корзина</h1>
    <div className='cart-container_1'>
      <section className='cart__products'>
        <ul className='cart__product-list'>
          {products.map((product) => (
            <CartProduct
            name={product.title} 
            mainPicture={product.mainPicture}
            price={product.price}
            onCardClick={onCardClick}
            id={product.productId}
            isChecked={isChecked}
            onCheckboxClick={handleCheckboxClick}
            />
          ))}
        </ul>
      </section>
      {/* <div className='cart__summary'>
        <form className='cart__form'>
          <h2 className='cart__subheader'>Сумма заказа</h2>
          <div className='cart-container_2'>
            <div className='cart__sum-text'>Товары ({products.length})</div>
            <div className='cart__sum-text'>{total} &#8381;</div>
          </div>
          <div className='cart-container_2'>
            <div className='cart__sum-text'>Скидка магазина</div>
            <div className='cart__sum-text'>- 1 000 &#8381;</div>
          </div>
          <div className='cart-container_2'>
            <p className='cart__sum-header'>Итого:</p>
            <div className='cart__sum'>{total} &#8381;</div>
          </div>
          <p className='cart__sum-caption'>Без учета стоимости доставки</p>
          <button type='submit' name='cart-submit' className='cart__submit-button hover_type_normal pointer'>Перейти к оформлению</button>
        </form>
      </div> */}
    </div>
    </main>
    </>
  )
}

