class ProductsApi {
  constructor(options) {
    this._url = options.url
    this._headers = options.headers
  }

  _getResponseData(res) {
    if (!res.ok) {
      return Promise.reject(`Ошибка: ${res.status}`);
    }
    return res.json();
  }

  getAllProducts() {
    return fetch(this._url + '/products/all', {
      method: 'GET',
      headers: this._headers
    })
      .then(this._getResponseData);
  }

  getProduct(id) {
    return fetch(this._url + `/products/by_id?productId=${id}`, {
      method: 'GET',
      headers: this._headers
    })
      .then(this._getResponseData);
  }

  getProductsById(products) {
    let savedPoducts = products.join(',');
    return fetch(this._url + `/products/by_id?productId=${savedPoducts}`, {
      method: 'GET',
      headers: this._headers
    })
      .then(this._getResponseData);
  }

  getTerms() {
    return fetch(this._url + '/products/terms', {
      method: 'GET',
      headers: this._headers
    })
      .then(this._getResponseData);
  }

  getReviews() {
    return fetch(this._url + '/testimonials', {
      method: 'GET',
      headers: this._headers
    })
      .then(this._getResponseData);
  }

  filterProducts(category = '', tags = '', search = '', offset = '', pageSize = '', sortBy = '', seed = 1, colors = '') {
    let sortByClean = sortBy.includes('-') ? sortBy.split('-')[0] : sortBy;
    let sortOrder = 'asc';
    if (sortBy.includes('-desc')) sortOrder = 'desc';

    // console.log(`/products/filter?category=${category}&tags=${tags}&search=${search}&offset=${offset}&pageSize=${pageSize}&sortBy=${sortByClean}&sortOrder=${sortOrder}&seed=${seed}`);

    return fetch(this._url + `/products/filter?category=${category}&tags=${tags}&search=${search}&offset=${offset}&pageSize=${pageSize}&sortBy=${sortByClean}&sortOrder=${sortOrder}&seed=${seed}&colors=${colors}`, {
      method: 'GET',
      headers: this._headers
    })
    .then(this._getResponseData);
  }

  getCdekCities(city) {
      return fetch(this._url + `/cdek/city?city=${city}`, {
      method: 'GET',
      headers: this._headers
    })
      .then(this._getResponseData);
  }

  getCdekTariff(city) {
    return fetch(this._url + `/cdek/tarifflist?codeTo=${city}`, {
    method: 'GET',
    headers: this._headers
  })
    .then(this._getResponseData);
}

  getDeliveryPoints(city) {
    return fetch(this._url + `/cdek/deliveryPoints?city_code=${city}`, {
    method: 'GET',
    headers: this._headers
  })
    .then(this._getResponseData);
  }

  postReview(data) {
    return fetch('https://dolina.shop/forms/testimonials.php', {
      method: 'POST',
      headers: this._headers,
      body: JSON.stringify({
        title: data.title,
        text: data.text,
        author: data.author,
        image: data.image
      })
    })
      .then(this._getResponseData);
  }

}

        
const productsApi = new ProductsApi({
  url: ' https://backend.dolina.shop',
  headers: {
    'Content-Type': 'application/json',
  }
});

export default productsApi;
